mapper.select:
select

from jd_order
where is_deleted = 0

    AND product_id = #{searchRequest.productId}


    AND customer like concat('%',#{searchRequest.costumer},'%')


    AND end_user like concat('%',#{searchRequest.endUser},'%')


    AND creator = #{searchRequest.creator}


    AND create_time > #{searchRequest.startDate}


    AND create_time < #{searchRequest.endDate}

order by order_id desc

mapper.update:
update jd_order
set is_deleted = 1
where order_id = #{orderId}
